	/*
	 * Method to set the shooter speed based on voltage
	 */
	public void setFeederSpeed(double speed)
	{
		// Set the Talon in voltage mode
		feeder.changeControlMode(CANTalon.TalonControlMode.PercentVbus);
		
		// Set the desired speed
		feeder.set(speed);
	}
	
	/*
	 * Method to set the shooter feeder speed for the PID control
	 */
	public void setFeederRPM(double RPM)
	{
		// Set the Talon in PID speed mode
		feeder.changeControlMode(CANTalon.TalonControlMode.Speed);

		// Set the desired speed
		feeder.set(RPM);
	}
	
	/* 
	 * Method to enable the shooter PID
	 */
	public void enableFeederPID()
	{
		feeder.enable();
	}
	
	/*
	 * Method to disable the shooter PID
	 */
	public void disableFeederPID()
	{
		// Disable the shooter
		feeder.disable();
	}
	
	/*
	 * Method to set the shooter speed based on voltage
	 */
	public void setShooterSpeed(double speed)
	{
		// Set the Talon in voltage mode
		shooter.changeControlMode(CANTalon.TalonControlMode.PercentVbus);
		
		// Set the desired speed
		shooter.set(speed);
	}
	
	/*
	 * Method to set the shooter speed based on the PID
	 */
	public void setShooterRPM(double RPM)
	{
		// Set the Talon in PID speed mode
		shooter.changeControlMode(CANTalon.TalonControlMode.Speed);

		// Set the desired speed
		shooter.set(RPM);
	}

	/* 
	 * Method to enable the shooter PID
	 */
	public void enableShooterPID()
	{
		shooter.enable();
	}
	
	/*
	 * Method to disable the shooter PID
	 */
	public void disableShooterPID()
	{
		// Disable the shooter
		shooter.disable();
	}

	/*
	 * Set the shooter PID to the specified values
	 */
	public void setShooterPID_Parameters(double F, double P, double I, double D)
	{
		// Read the F, P, I and D parameters from the smartdashboard
		shooter.setF(F);
		shooter.setP(P);
		shooter.setI(I);
		shooter.setD(D);
	}
	
	/*
	 * Method to set the shooter PID values from the smartdashboard
	 */
	public void setShooterPID_ParametersFromSmartdashboard()
	{
		// Read the F, P, I and D parameters from the smartdashboard
		shooter.setF(SmartDashboard.getNumber("Shooter F", 0.0));
		shooter.setP(SmartDashboard.getNumber("Shooter P", 0.0));
		shooter.setI(SmartDashboard.getNumber("Shooter I", 0.0));
		shooter.setD(SmartDashboard.getNumber("Shooter D", 0.0));
	}
	
	/*
	 * Method to set the shooter speed from the value read from the smartdashboard
	 */
	public void setShooterRPM_FromSmartdashboard()
	{
		// Set the shooter speed from the smartdashboard
		setShooterRPM(SmartDashboard.getNumber("Shooter RPM Setpoint", 1000));
	}
